//Product Interfacepackage singletonfactorybuilder;interface Vehicle { void manufacture();}//Concrete Products (simplified)class Car implements Vehicle { private String details; public Car(String details) { this.details = details; } @Override public void manufacture() {     System.out.println("Manufacturing Car with details: " + details); }}class Truck implements Vehicle { private String details; public Truck(String details) { this.details = details; } @Override public void manufacture() {     System.out.println("Manufacturing Truck with details: " + details); }}//Factory Classpublic class VehicleFactory { // The factory internally gets the Singleton instance private ConfigurationManager config = ConfigurationManager.getInstance(); public Vehicle createVehicle(String type) {     // Factory uses the global config setting when creating the product     System.out.println("[Factory Log] Using global config quality level: " + config.getRequiredQualityLevel());     String details = "Standard build (QC Level " + config.getRequiredQualityLevel() + ")";     switch (type.toLowerCase()) {         case "car":             return new Car(details);         case "truck":             return new Truck(details);         default:             throw new IllegalArgumentException("Unknown vehicle type");     } }}